import React from "react";

import "./EmailForm.scss";

const EmailForm = () => {
  const [message, setMessage] = React.useState("");
  const [input, setInput] = React.useState("");
  const [inputHasError, setInputHasError] = React.useState(true);

  const inputChangeHandler = (event) => {
    setInput(event.target.value);
  };

  const emailValidation = () => {
    const emailRegex =
      /^(([^<>()[\]\.,;:\s@\"]+(\.[^<>()[\]\.,;:\s@\"]+)*)|(\".+\"))@(([^<>()[\]\.,;:\s@\"]+\.)+[^<>()[\]\.,;:\s@\"]{2,})$/i;
    return !(!input || emailRegex.test(input) === false);
  };

  const countryValidation = () => {
    const countryRegex = /.co$/;
    return !input || countryRegex.test(input) === false;
  };

  const onSubmit = () => {
    const isEmailValid = emailValidation();
    const countryValid = countryValidation();

    if (Object.keys(input).length === 0) {
      setInputHasError(true);
      setMessage("Email adress is required!");
    } else if (!countryValid) {
      setInputHasError(true);
      setMessage("We are not accepting subscriptions from Colombia emails!");
    } else if (!isEmailValid) {
      setInputHasError(true);
      setMessage("Email Address not valid!");
    } else {
      setInputHasError(false);
      setMessage("Thanks for subscribing!");
    }
  };

  const preventDefault = (e) => {
    e.preventDefault();
  };

  const messageClass = inputHasError ? "error-message" : "approved-message";

  return (
    <div className="formBox">
      <form
        className="form"
        onSubmit={preventDefault}
        action="#"
        id="subscriptionForm"
      >
        <div class="vl"></div>
        <input
          id="email_input"
          type="text"
          className="emailInput"
          value={input}
          onChange={inputChangeHandler}
          name="email_input"
          placeholder="Type your email address hereâ€¦"
        />
        <input
          id="submitButton"
          type="image"
          className="submitButton"
          alt="Submit"
          src=".Images/ic_arrow.png"
        ></input>
        {/* <button className="submitButton" onClick={onSubmit}></button> */}
      </form>
      <div className={messageClass}>{message}</div>
    </div>
  );
};

export default EmailForm;
